import React, { Component } from 'react';
import { connect } from 'react-redux';
import SimpleReactValidator from 'simple-react-validator';
import toastr from "reactjs-toastr";
import axios from 'axios';
import { FormGroup, FormControl, FormLabel } from 'react-bootstrap';
import { getUserdetails} from "./../store/actions/userActions";
import {getAllgames} from './../store/actions/gamesActions';
import parseJwt from './../store/helpers/common';
import $ from "jquery";
import { sendMessage} from "./../store/actions/messageActions";
import PhoneInput from 'react-phone-number-input';
import 'react-phone-number-input/style.css';

class profile extends Component {
    constructor(props) {
        super(props);
        this.profileValidator = new SimpleReactValidator();
        this.state = {
            games:'',
            message: '',
            firstname: '',
            email:'',
            username:'',
            // lastname: '',
            phone: '',
            password: '',
            profile_pic:'',
            cpassword: '',
            xboxid: '',
            psnid: '',
            pccodmw:'',
            pccodaw:'',
            pccodbo3:'',
            pccodm:'',
            facebook: '',
            instagram: '',
            twitter: '',
            youtube: '',
            totalItems: '',
            isSubmitcred:false,
            isSubmitgamertag:false,
            isSubmitsocial:false,
            currid:'',
            propic:'',
            codmwwins:'' ,codawwins:'',codbo3wins:'',codmwins:'',
            codmwtotal:'' ,codawtotal:'',codbo3total:'',codmtotal:'',
            codmwscore:'' ,codawscore:'',codbo3score:'',codmscore:'',
            invitename:'',
            invitenumber:'',
            invitemessage:''
        }
    }
    validateLoginForm = (e) => {
        let validate = this.profileValidator;
        if (validate.allValid()) {
        } else {
            validate.showMessages();
            this.forceUpdate();
        }
        this.setState({ [e.target.name]: e.target.value });
        e.preventDefault();
    }
    invitecheck = (e) =>{
        this.setState({ [e.target.name]: e.target.value });
    }
    inviteUser = () =>{
        let validate = this.profileValidator;
        if (validate.allValid()) {
        const {invitenumber,invitename, firstname} = this.state;
        const invitemessage = "hey "+ invitename +" , "+ firstname +" invited you to join the Team Envy Gaming & Esports Community. Play your favorite video games for cool rewards & prizes. Get started today";
       this.props.dispatch(sendMessage(invitenumber,invitemessage));
       this.setState({ invitenumber: '',invitename:''  });
    } else {
        validate.showMessages();
        // rerender to show messages for the first time
        this.forceUpdate();
    }
    }
    componentDidMount() {
        if (localStorage.getItem('token')) {
        const currdetails = parseJwt(localStorage.getItem('token'));
        const currid = currdetails.sub;
        this.setState({currid:currid});
        this.props.dispatch(getUserdetails(currid));
        }
        this.props.dispatch(getAllgames());
    }

    updateCreds = (e) => {
  if(e === 'details'){
            const { firstname, phone, password ,cpassword ,currid } = this.state;
            this.setState({isSubmitcred:true});
            const request = new Request(`https://xrsports.gg/team/public/user/update-userdetails`, {
            method: "POST",
            body: JSON.stringify({ firstname, password, phone ,currid }),
            headers: new Headers({
              "Content-Type": "application/json",
              "X-Auth-Token": `${localStorage.getItem("token")}`
            })
          });
                if (password !== cpassword) {
                    this.setState({isSubmitcred:false});
                    toastr.error('Passwords Dont Match', { displayDuration: 1500 });                
                } else {
                    return fetch(request)
                    .then(res => res.json())
                    .then(data => {
                      if (data.ResponseCode === "1") {
                        this.setState({isSubmitcred:false});
                        this.props.dispatch(getUserdetails(currid));
                        toastr.success(data.ResponseText, { displayDuration: 1500 });
                      } else {
                        toastr.error(data.ResponseText, { displayDuration: 1500 });
                      }
                    })
                    .catch(err => {
                      console.log(err);
                    });
                }

        }else if(e === 'gamertag'){
            const { xboxid, psnid,pccodmw,pccodaw,pccodbo3,pccodm,currid } = this.state;
            this.setState({isSubmitgamertag:true});
            const request = new Request(`https://xrsports.gg/team/public/user/update-usergamertag`, {
            method: "POST",
            body: JSON.stringify({ xboxid,pccodmw, psnid, pccodaw,pccodbo3,pccodm,currid }),
            headers: new Headers({
              "Content-Type": "application/json",
              "X-Auth-Token": `${localStorage.getItem("token")}`
            })
          });
          return fetch(request)
            .then(res => res.json())
            .then(data => {
              if (data.ResponseCode === "1") {
                this.setState({isSubmitgamertag:false});
                this.props.dispatch(getUserdetails(currid));
                toastr.success(data.ResponseText, { displayDuration: 1500 });
              } else {
                toastr.error(data.ResponseText, { displayDuration: 1500 });
              }
            })
            .catch(err => {
              console.log(err);
            });
        }else if(e === 'social'){
            const { facebook,twitter,instagram,youtube,currid } = this.state;
            this.setState({isSubmitsocial:true});
            const request = new Request(`https://xrsports.gg/team/public/user/update-usersociallinks`, {
            method: "POST",
            body: JSON.stringify({ facebook,twitter,instagram,youtube,currid }),
            headers: new Headers({
              "Content-Type": "application/json",
              "X-Auth-Token": `${localStorage.getItem("token")}`
            })
          });
          return fetch(request)
            .then(res => res.json())
            .then(data => {
              if (data.ResponseCode === "1") {
                this.setState({isSubmitsocial:false});
                this.props.dispatch(getUserdetails(currid));
                toastr.success(data.ResponseText, { displayDuration: 1500 });
              } else {
                toastr.error(data.ResponseText, { displayDuration: 1500 });
              }
            })
            .catch(err => {
              console.log(err);
            });
        }else if(e === 'propic'){

        const {currid} = this.state;
        let request;
        let formData = new FormData();
        formData.append('profile_pic', $('#propicfield')[0].files[0]);
        formData.append('currid', currid);
           request = {
            method: 'POST',
            url: `https://xrsports.gg/team/public/user/update-propic`,
            headers: { 'Content-Type': 'application/json', 'X-Auth-Token': `${localStorage.getItem('token')}` },
            data: formData
          };

        axios(request).then((data) => {
          if (data.data.ResponseCode === "1") {
            this.props.dispatch(getUserdetails(currid));
            toastr.success(data.data.ResponseText, { displayDuration: 1500 })
             }else {
            toastr.error(data.data, { displayDuration: 1500 })
          }
        });
        }
    }
    componentWillReceiveProps(nextProps) {
        this.setState({
            totalItems: nextProps.userdata,
            firstname: nextProps.userdata.firstname,
            // lastname: nextProps.userdata.lastname,
            username: nextProps.userdata.nickname,
            email: nextProps.userdata.email,
            phone: nextProps.userdata.phone,
            xboxid: nextProps.userdata.xboxid,
            psnid: nextProps.userdata.psnid,
            pccodmw: nextProps.userdata.pccodmw,
            pccodaw: nextProps.userdata.pccodaw,
            pccodbo3: nextProps.userdata.pccodbo3,
            pccodm: nextProps.userdata.pccodm,
            facebook: nextProps.userdata.facebook,
            instagram: nextProps.userdata.instagram,
            twitter: nextProps.userdata.twitter,
            youtube: nextProps.userdata.youtube,
            profile_pic: nextProps.userdata.profile_pic,
            codmwwins:nextProps.userdata.codmwwins ,  codawwins:nextProps.userdata.codawwins,codbo3wins:nextProps.userdata.codbo3wins,codmwins:nextProps.userdata.codmwins,
            codmwtotal:nextProps.userdata.codmwtotal ,codawtotal:nextProps.userdata.codawtotal,codbo3total:nextProps.userdata.codbo3total,codmtotal:nextProps.userdata.codmtotal,
            codmwscore:nextProps.userdata.codmwscore ,codawscore:nextProps.userdata.codawscore,codbo3score:nextProps.userdata.codbo3score,codmscore:nextProps.userdata.codmscore,
            games: nextProps.gamesdata,
            // propic : nextProps.userdata.profile_pic
            });
                // if(this.state.propic === ''){
                // this.setState({ profile_pic: nextProps.userdata.profile_pic, });
                // }else{
                // this.setState({ profile_pic: nextProps.userdata.profile_pic, });
                // }
    }
    render() {
        const{codmwwins ,codawwins,codbo3wins,codmwins,codmwtotal ,codawtotal,codbo3total,codmtotal,codmwscore ,codawscore,codbo3score,codmscore} = this.state;
        return (

            <div className="container">
            <div id="mainpage" className="main_box col-md-12 p-0">
                <div className="main-content profile-section">
                    <div className="main-content support frontline text-left mt-3">
                    <div className="position-relative">
            <div id="demo" className="collapse">

            <div className="container-fluid h-100">
			<div className="row justify-content-center h-100">

				<div className="col-md-12 col-lg-12 chat">
					<div className="card">
                    <div className="card-header headd">
                        <h2 className="mb-0">Group Chat</h2>
                        </div>
						<div className="card-header msg_head">
							<div className="d-flex bd-highlight">
								<div className="img_cont">
									<img src="https://static.turbosquid.com/Preview/001292/481/WV/_D.jpg" className="rounded-circle user_img" alt="">
									</img>
                                    <span className="online_icon"></span>
								</div>
								<div className="user_info">
									<span>Chat with Khalid</span>
									{/* <p>1767 Messages</p> */}
								</div>
								{/* <div className="video_cam">
									<span><i className="fas fa-video"></i></span>
									<span><i className="fas fa-phone"></i></span>
								</div> */}
							</div>
							<span id="action_menu_btn"><i className="fas fa-sort-down"></i></span>
							<div className="action_menu">
								<ul>
									<li><i className="fas fa-user-circle"></i> View profile</li>
									<li><i className="fas fa-users"></i> Add to close friends</li>
									<li><i className="fas fa-plus"></i> Add to group</li>
									<li><i className="fas fa-ban"></i> Block</li>
								</ul>
							</div>
						</div>
						<div className="card-body msg_card_body">
							<div className="d-flex justify-content-start mb-2">
								<div className="img_cont_msg">
									<img src="https://static.turbosquid.com/Preview/001292/481/WV/_D.jpg" className="rounded-circle user_img_msg" alt="">
                                    </img>
                                </div>

								<div className="msg_cotainer">
                                    <p>Cadi09: <span className="msg_time">8:40 AM, Today</span></p>
									Hi, how are you samim?
									{/* <span className="msg_time">8:40 AM, Today</span> */}
								</div>
							</div>
							<div className="d-flex justify-content-end mb-2">
								<div className="msg_cotainer_send">
                                <p><span className="msg_time">8:40 AM, Today</span> :Cadi09</p>

									Hi Khalid i am good tnx how about you?
								</div>
								<div className="img_cont_msg">
							<img src="data:image/jpeg;base64," className="rounded-circle user_img_msg">
                            </img>
                                </div>
							</div>
							<div className="d-flex justify-content-start mb-2">
								<div className="img_cont_msg">
									<img src="https://static.turbosquid.com/Preview/001292/481/WV/_D.jpg" className="rounded-circle user_img_msg">
                                    </img>
                                </div>
								<div className="msg_cotainer">
                                <p>Cadi09: <span className="msg_time">8:40 AM, Today</span></p>

									I am good too, thank you for your chat template
								</div>
							</div>
							<div className="d-flex justify-content-end mb-2">
								<div className="msg_cotainer_send">
                                <p><span className="msg_time">8:40 AM, Today</span> :Cadi09</p>

									You are welcome
								</div>
								<div className="img_cont_msg">
							<img src="data:image/jpeg;base64," className="rounded-circle user_img_msg">
                            </img>
                                </div>
							</div>
							<div className="d-flex justify-content-start mb-2">
								<div className="img_cont_msg">
									<img src="https://static.turbosquid.com/Preview/001292/481/WV/_D.jpg" className="rounded-circle user_img_msg" alt="">
                                    </img>
                                </div>
								<div className="msg_cotainer">
                                <p>Cadi09: <span className="msg_time">8:40 AM, Today</span></p>

									I am looking for your next templates
								</div>
							</div>
							<div className="d-flex justify-content-end mb-2">
								<div className="msg_cotainer_send">
                                <p><span className="msg_time">8:40 AM, Today</span> :Cadi09</p>

									Ok, thank you have a good day
								</div>
								<div className="img_cont_msg">
                        <img src="data:image/jpeg;base64,
                        " className="rounded-circle user_img_msg">
								</img></div>
							</div>
							<div className="d-flex justify-content-start mb-2">
								<div className="img_cont_msg">
									<img src="https://static.turbosquid.com/Preview/001292/481/WV/_D.jpg" className="rounded-circle user_img_msg"></img>
								</div>
								<div className="msg_cotainer">
                                <p>Cadi09: <span className="msg_time">8:40 AM, Today</span></p>

									Bye, see you
								</div>
							</div>
						</div>
						<div className="card-footer">
							<div className="input-group">
								{/* <div className="input-group-append">
									<span className="input-group-text attach_btn"><i className="fas fa-paperclip"></i></span>
								</div> */}
								<textarea name="" className="form-control type_msg" placeholder="Type your message..."></textarea>
								<div className="input-group-append">
									<span className="input-group-text send_btn"><i className="fas fa-location-arrow"></i><i className="fas fa-grin-alt"></i><i className="fas fa-ellipsis-v"></i></span>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
</div>
 </div>
                    <h2 className="commonheading">My Profile</h2>

                <hr className="thick tour mb-2"/>
                        <ul className="nav nav-tabs py-3">
                            <li className="nav-item">
                            <a className="nav-link active" href="#tab1" data-toggle="tab">GENERAL</a>
                            </li>
                            <li className="nav-item">
                            <a className="nav-link" href="#tab2" data-toggle="tab">USERNAMES</a>
                            </li>
                            <li className="nav-item">
                            <a className="nav-link" href="#tab3" data-toggle="tab">STATS</a>
                            </li>
                            <li className="nav-item">
                            <a className="nav-link" href="#tab5" data-toggle="tab">INVITE A FRIEND</a>
                            </li>
                        </ul>
                        <div className="tab-content">
                           <div className="tab-pane active" id="tab1">
                                <div className="full userpro mt-0 p-0">

                                   <div id="post-352">
                                    <div className="entry-content entry">
                                    <div className="row">
                                    <FormGroup>
                                    <FormLabel>FirstName</FormLabel>
                                    <FormControl type="text" name="firstname" placeholder="Enter Your Firstname" value={this.state.firstname} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>
                                    <FormGroup>
                                    <FormLabel>UserName</FormLabel>
                                    <FormControl type="text" name="username" value={this.state.username} readOnly />
                                    </FormGroup>

                                    </div>
                                    <div className="row">


                                    <FormGroup >
                                    <FormLabel>Phone</FormLabel>
                                    <FormControl type="text" name="phone" placeholder="Enter Your Phone Number" value={this.state.phone} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>
                                    <FormGroup >
                                    <FormLabel>E-mail</FormLabel>
                                    <FormControl type="text" name="email" placeholder="Enter Your E-Mail" value={this.state.email} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>
                                    </div>
                                    <div className="row ">


                                    <FormGroup>
                                    <FormLabel>Password</FormLabel>
                                    <FormControl type="password" name="password" placeholder="Enter Your Password" value={this.state.password} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>
                                    <FormGroup>
                                    <FormLabel>Repeat Password</FormLabel>
                                    <FormControl type="password" name="cpassword" placeholder="Confirm Password" value={this.state.cpassword} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>
                                    </div>
                                    <p className="form-submit">
                                    <button type="submit"  onClick={(e) => this.updateCreds('details')} disabled={this.state.isSubmitcred} className="btn btn-red btn-block btn-lg profilebuttons">{this.state.isSubmitcred ? 'PLEASE WAIT..' : 'UPDATE'}</button>
                                    </p>
                                    </div>
                                    </div>
                                    </div>
