                                    </FormGroup>


                                    <FormGroup >
                                    <FormLabel>Xbox Gamertag</FormLabel>
                                    <FormControl type="text" name="xboxid" placeholder="Enter Your XBOX Gamertag" value={this.state.xboxid} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>
                                    </div>

