                                    <FormLabel>CALL OF DUTY ADVANCE WARFARE</FormLabel>
                                    <FormControl type="text" name="pccodaw" placeholder="" value={this.state.pccodaw} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>
                                    </div>

                                    <div className="row">
                                    <FormGroup >
                                    <FormLabel>CALL OF DUTY BLACK OPS 3</FormLabel>
                                    <FormControl type="text" name="pccodbo3" placeholder="" value={this.state.pccodbo3} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>

                                    <FormGroup>
                                    <FormLabel>CALL OF DUTY MOBILE</FormLabel>
                                    <FormControl type="text" name="pccodm" placeholder="" value={this.state.pccodm} onChange={e => this.validateLoginForm(e)} />
                                    </FormGroup>
