                                        <p className="form-submit">
                                        <button type="submit"  onClick={(e) => this.updateCreds('gamertag')} disabled={this.state.isSubmitgamertag} className="btn btn-red btn-block btn-lg profilebuttons">{this.state.isSubmitgamertag ? 'PLEASE WAIT..' : 'UPDATE'}</button>
                                        </p>
                                </div>
                                <div className="full userpro entry mt-0 pt-0">
