                                    <h3>
                                    {post.game_name === 'codmw'?codmwscore:post.game_name === 'codaw'?codawscore:post.game_name === 'codbo3'?codbo3score:post.game_name === 'codm'?codmscore:''}
                                    </h3>
                                </div>
                            </div> 
                        </div>
                        <div className="col-lg-3">
                            <div className="diffgamestat">
                                <div>
                                    <h3>
                                    {post.game_name === 'codmw'?codmwtotal:post.game_name === 'codaw'?codawtotal:post.game_name === 'codbo3'?codbo3total:post.game_name === 'codm'?codmtotal:''}
                                    </h3>
                                </div>
                            </div> 
                        </div>
                        <div className="col-lg-3">
                            <div className="diffgamestat">
                                <div>
                                <h3>
                                    {post.game_name === 'codmw'?codmwwins:post.game_name === 'codaw'?codawwins:post.game_name === 'codbo3'?codbo3wins:post.game_name === 'codm'?codmwins:''}
                                    </h3>
                                </div>
                            </div> 
                        </div>
                        </div>
                    </div>
                   )) :''
            }                                                                            
                        </div> 
                            </div>
                            <div className="tab-pane" id="tab4">
                            </div>
                            <div className="tab-pane" id="tab5">
                                <img src="https://res.cloudinary.com/dnv0dij0y/image/upload/v1575617451/FCD-Invitation_tezifs.png" alt=""/>
                                <div className="full userpro entry mt-4 p-0">
                                    <div className="row py-4">
                                        <div className="col-md-6">
                                        <label className="w-100 text-left py-0" htmlFor="first-name">Name</label> 

                                        <p className="form-username">
                                            <input className="form-control" type="text" name="invitename" value={this.state.invitename} onChange={(e)=>this.invitecheck(e)} />
                                            {this.profileValidator.message('invitename', this.state.invitename, 'required')}
                                        </p>
                                        </div>

                                        <div className="col-md-6">
                                        <label className="w-100 text-left py-0" htmlFor="first-name">Phone</label> 
                                        <p className="form-username">
                                            {/* <input className="form-control" type="text" name="invitenumber" id="invitenumber" value={this.state.invitenumber} onChange={(e)=>this.invitecheck(e)} /> */}
                                            <PhoneInput defaultCountry="US" internationalIcon={()=><span ><i className="fa fa-phone  icon-circle bg-danger"></i></span> }  type="text" value={ this.state.invitenumber } onChange={invitenumber => this.setState({ invitenumber })} className="form-control " placeholder="Phone" />
                                            {/* <PhoneInput country="US" value={this.state.invitenumber}  onChange={invitenumber => this.setState({ invitenumber })} /> */}
                                            {this.profileValidator.message('invitenumber', this.state.invitenumber, 'required')}
                                        </p>
                                        </div>
                                        </div>
                                        <div id="phonediv" ></div>
                                        <p className="form-submit">
                                            <input type="submit" value="Send Invitation" className="btnup" onClick={(e)=>this.inviteUser(e)} name="sendinvite" id="sendinvite"/>
                                        </p>        				
                                </div>
                            </div>
                        </div>  

                    </div>

                </div>
                </div>
                </div>
        );
    }
}

