    validateLoginForm = (e) => {
        let validate = this.profileValidator;
        if (validate.allValid()) {
        } else {
            validate.showMessages();
            this.forceUpdate();
        }
        this.setState({ [e.target.name]: e.target.value });
        e.preventDefault();
    }
    invitecheck = (e) =>{
        this.setState({ [e.target.name]: e.target.value });
    }
    inviteUser = () =>{
        let validate = this.profileValidator;
        if (validate.allValid()) {
        const {invitenumber,invitename, firstname} = this.state;
        const invitemessage = "hey "+ invitename +" , "+ firstname +" invited you to join the Team Envy Gaming & Esports Community. Play your favorite video games for cool rewards & prizes. Get started today";
       this.props.dispatch(sendMessage(invitenumber,invitemessage));
       this.setState({ invitenumber: '',invitename:''  });
    } else {
        validate.showMessages();
        // rerender to show messages for the first time
        this.forceUpdate();
    }
    }
    componentDidMount() {
        if (localStorage.getItem('token')) {
        const currdetails = parseJwt(localStorage.getItem('token'));
        const currid = currdetails.sub;
        this.setState({currid:currid});
        this.props.dispatch(getUserdetails(currid));
        }
        this.props.dispatch(getAllgames());
    }

    updateCreds = (e) => {
  if(e === 'details'){
            const { firstname, phone, password ,cpassword ,currid } = this.state;
            this.setState({isSubmitcred:true});
            const request = new Request(`https://xrsports.gg/team/public/user/update-userdetails`, {
            method: "POST",
            body: JSON.stringify({ firstname, password, phone ,currid }),
            headers: new Headers({
              "Content-Type": "application/json",
              "X-Auth-Token": `${localStorage.getItem("token")}`
            })
          });
                if (password !== cpassword) {
                    this.setState({isSubmitcred:false});
                    toastr.error('Passwords Dont Match', { displayDuration: 1500 });                
                } else {
                    return fetch(request)
                    .then(res => res.json())
                    .then(data => {
                      if (data.ResponseCode === "1") {
                        this.setState({isSubmitcred:false});
                        this.props.dispatch(getUserdetails(currid));
                        toastr.success(data.ResponseText, { displayDuration: 1500 });
                      } else {
                        toastr.error(data.ResponseText, { displayDuration: 1500 });
                      }
                    })
                    .catch(err => {
                      console.log(err);
                    });
                }
       
        }else if(e === 'gamertag'){
            const { xboxid, psnid,pccodmw,pccodaw,pccodbo3,pccodm,currid } = this.state;
            this.setState({isSubmitgamertag:true});
            const request = new Request(`https://xrsports.gg/team/public/user/update-usergamertag`, {
            method: "POST",
            body: JSON.stringify({ xboxid,pccodmw, psnid, pccodaw,pccodbo3,pccodm,currid }),
            headers: new Headers({
              "Content-Type": "application/json",
              "X-Auth-Token": `${localStorage.getItem("token")}`
            })
          });
          return fetch(request)
            .then(res => res.json())
            .then(data => {
              if (data.ResponseCode === "1") {
                this.setState({isSubmitgamertag:false});
                this.props.dispatch(getUserdetails(currid));
                toastr.success(data.ResponseText, { displayDuration: 1500 });
              } else {
                toastr.error(data.ResponseText, { displayDuration: 1500 });
              }
            })
            .catch(err => {
              console.log(err);
            });
        }else if(e === 'social'){
            const { facebook,twitter,instagram,youtube,currid } = this.state;
            this.setState({isSubmitsocial:true});
            const request = new Request(`https://xrsports.gg/team/public/user/update-usersociallinks`, {
            method: "POST",
            body: JSON.stringify({ facebook,twitter,instagram,youtube,currid }),
            headers: new Headers({
              "Content-Type": "application/json",
              "X-Auth-Token": `${localStorage.getItem("token")}`
            })
          });
          return fetch(request)
            .then(res => res.json())
            .then(data => {
              if (data.ResponseCode === "1") {
                this.setState({isSubmitsocial:false});
                this.props.dispatch(getUserdetails(currid));
                toastr.success(data.ResponseText, { displayDuration: 1500 });
              } else {
                toastr.error(data.ResponseText, { displayDuration: 1500 });
              }
            })
            .catch(err => {
              console.log(err);
            });
        }else if(e === 'propic'){
                
        const {currid} = this.state;
        let request;
        let formData = new FormData();
        formData.append('profile_pic', $('#propicfield')[0].files[0]);
        formData.append('currid', currid);
           request = {
            method: 'POST',
            url: `https://xrsports.gg/team/public/user/update-propic`,
            headers: { 'Content-Type': 'application/json', 'X-Auth-Token': `${localStorage.getItem('token')}` },
            data: formData
          };
        
        axios(request).then((data) => {
          if (data.data.ResponseCode === "1") {
            this.props.dispatch(getUserdetails(currid));
            toastr.success(data.data.ResponseText, { displayDuration: 1500 })
             }else {
            toastr.error(data.data, { displayDuration: 1500 })
          }
        });
        }
    }
